mapper.select:
SELECT * FROM FREE_BOARD WHERE STATUS='Y'
AND BOARD_AREA = #{state}
AND ( BOARD_TITLE like '%' || #{search} || '%' OR BOARD_WRITER like '%' || #{search} || '%' OR BOARD_CONTENT like '%' || #{search} || '%' )

mapper.update:
UPDATE FREE_BOARD
SET STATUS = 'N'
WHERE FREE_BNO = #{fno}

UPDATE FREE_BOARD
SET BOARD_TITLE = ${boardTitle},
    BOARD_CONTENT = ${boardContent}
WHERE FREE_BNO = ${boardNo}
