mapper.select:
SELECT COUNT(*) FROM FREE_BOARD WHERE STATUS='Y'
AND BOARD_AREA = #{state}
AND ( BOARD_TITLE like '%' || #{search} || '%' OR BOARD_WRITER like '%' || #{search} || '%' OR BOARD_CONTENT like '%' || #{search} || '%' )

mapper.update:
UPDATE FREE_BOARD
SET STATUS = 'N'
WHERE FREE_BNO = #{fno}

UPDATE REPLY
SET STATUS = 'N'
WHERE FREE_BNO = ${freeBno}
    AND REPLY_NO = ${replyNo}
    AND MEMBER_NO = ${memberNo}
