mapper.select:
SELECT COUNT(*) FROM RESTAURANT res WHERE TRIM(res.STATE) like '%' || #{state} || '%'
AND res.RES_NO IN (SELECT res_tag.RES_NO FROM RES_HASHTAG res_tag INNER JOIN HASHTAG h ON res_tag.HASHTAG_ID = h.HASHTAG_ID WHERE h.HASHTAG IN
#{hashtag}
)

SELECT * FROM RESTAURANT res WHERE TRIM(res.STATE) like '%' || #{state} || '%'
AND res.RES_NO IN (SELECT res_tag.RES_NO FROM RES_HASHTAG res_tag INNER JOIN HASHTAG h ON res_tag.HASHTAG_ID = h.HASHTAG_ID WHERE h.HASHTAG IN
#{hashtag}
)

mapper.insert:
INSERT INTO RES_IMG SELECT SEQ_IMG_NO.NEXTVAL AS IMG_NO , R.* FROM (
SELECT #{img.originName} as ORIGIN_NAME, #{img.changeName} as CHANGE_NAME, #{img.revNo} as REV_NO, #{img.imgLevel} as IMG_LEVEL FROM DUAL
) R
